    private void EnterBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EnterBtnActionPerformed
        try{
            this.Barber = Integer.parseInt((String) Barbers.getSelectedItem());
            this.Customer = Integer.parseInt(Customers.getText());
            this.chairs = Integer.parseInt((String) Chairs.getSelectedItem());

            SleepingBarber sBarber = new SleepingBarber(Customer, chairs, Barber);
            Session s = new Session(Barber, Customer, chairs);
            s.setSize(970, 542);
            s.setVisible(true);
            this.setVisible(false);
            Thread t = new Thread(new Thread(){
                public void run(){
                    try {
                        sBarber.Start(s);
                    } catch (InterruptedException ex) {
                        Logger.getLogger(InputFrame.class.getName()).log(Level.SEVERE, null, ex);
                    }
                }
            });
            t.start();
        }
        catch(NumberFormatException e){
            System.out.println("Enter numbers");
        }

    }//GEN-LAST:event_EnterBtnActionPerformed

